import React, { useEffect, useState } from 'react';
import './ChatMessages.css';

interface ChatMessageProps {
  type: 'user' | 'bot';
  content: string;
  isLoading?: boolean;
  onLoadComplete?: () => void;
}

const LoadingDots: React.FC = () => (
  <div className="loading-dots">
    <div className="loading-dot" />
    <div className="loading-dot" />
    <div className="loading-dot" />
  </div>
);

const ChatMessage: React.FC<ChatMessageProps> = ({
  type,
  content,
  isLoading = false,
  onLoadComplete
}) => {
  const [isVisible, setIsVisible] = useState(false);
  const [displayedContent, setDisplayedContent] = useState('');
  const [isTyping, setIsTyping] = useState(false);

  useEffect(() => {
    if (type === 'bot' && !isLoading) {
      // Simulate typing effect for bot messages
      setIsTyping(true);
      let currentIndex = 0;
      const typingInterval = setInterval(() => {
        if (currentIndex <= content.length) {
          setDisplayedContent(content.slice(0, currentIndex));
          currentIndex++;
        } else {
          clearInterval(typingInterval);
          setIsTyping(false);
          onLoadComplete?.();
        }
      }, 20); // Adjust typing speed here

      return () => clearInterval(typingInterval);
    } else {
      setDisplayedContent(content);
    }
  }, [content, type, isLoading, onLoadComplete]);

  useEffect(() => {
    // Trigger entrance animation
    const timer = setTimeout(() => setIsVisible(true), 50);
    return () => clearTimeout(timer);
  }, []);

  const renderContent = () => {
    if (isLoading) {
      return <LoadingDots />;
    }

    if (type === 'bot' && isTyping) {
      return (
        <div className="typing-animation">
          {displayedContent}
          <span className="typing-cursor">|</span>
        </div>
      );
    }

    // For user messages or completed bot messages
    return (
      <div
        dangerouslySetInnerHTML={{
          __html: displayedContent
            .replace(/<strong>(.*?)<\/strong>/g, '<strong>$1</strong>')
            .replace(/<em>(.*?)<\/em>/g, '<em>$1</em>')
            .replace(/<code>(.*?)<\/code>/g, '<code>$1</code>')
            .replace(/<a href="(.*?)">(.*?)<\/a>/g, '<a href="$1" target="_blank" rel="noopener noreferrer">$2</a>')
        }}
      />
    );
  };

  return (
    <div
      className={`chatbot-msg ${type} ${isLoading ? 'loading' : ''} ${isVisible ? 'visible' : ''}`}
      style={{ opacity: isVisible ? 1 : 0 }}
      aria-label={`${type === 'user' ? 'You' : 'AI Assistant'}`}
    >
      {renderContent()}
    </div>
  );
};

export default ChatMessage;
