import React, { useEffect, useState, useRef } from 'react';
import './ChatMessages.css';

interface ChatMessageProps {
  type: 'user' | 'bot';
  content: string;
  isLoading?: boolean;
  onLoadComplete?: () => void;
  isNew?: boolean;
}

const LoadingDots: React.FC = () => (
  <div className="loading-dots">
    <div className="loading-dot" />
    <div className="loading-dot" />
    <div className="loading-dot" />
  </div>
);

const ChatMessage: React.FC<ChatMessageProps> = ({
  type,
  content,
  isLoading = false,
  onLoadComplete,
  isNew = true
}) => {
  const [isVisible, setIsVisible] = useState(false);
  const [displayedContent, setDisplayedContent] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const hasAnimated = useRef(false);

  useEffect(() => {
    // For user messages, show immediately
    if (type === 'user') {
      setIsVisible(true);
      setDisplayedContent(content);
      hasAnimated.current = true;
      return;
    }

    // For bot messages, handle animation
    if (isNew && !hasAnimated.current) {
      const timer = setTimeout(() => {
        setIsVisible(true);
        hasAnimated.current = true;
      }, 50);
      return () => clearTimeout(timer);
    } else {
      setIsVisible(true);
      setDisplayedContent(content);
    }
  }, [isNew, content, type]);

  useEffect(() => {
    // Only apply typing effect for new bot messages
    if (type === 'bot' && !isLoading && isNew && !hasAnimated.current) {
      setIsTyping(true);
      let currentIndex = 0;
      const typingInterval = setInterval(() => {
        if (currentIndex <= content.length) {
          setDisplayedContent(content.slice(0, currentIndex));
          currentIndex++;
        } else {
          clearInterval(typingInterval);
          setIsTyping(false);
          onLoadComplete?.();
          hasAnimated.current = true;
        }
      }, 20);

      return () => clearInterval(typingInterval);
    } else if (type === 'bot' && !isNew) {
      // For old bot messages, just display the content
      setDisplayedContent(content);
    }
  }, [content, type, isLoading, onLoadComplete, isNew]);

  const renderContent = () => {
    if (isLoading && isNew && type === 'bot') {
      return <LoadingDots />;
    }

    if (type === 'bot' && isTyping && isNew) {
      return (
        <div className="typing-animation">
          {displayedContent}
          <span className="typing-cursor">|</span>
        </div>
      );
    }

    // For user messages or completed bot messages
    return (
      <div
        dangerouslySetInnerHTML={{
          __html: displayedContent
        }}
      />
    );
  };

  return (
    <div
      className={`chatbot-msg ${type} ${isLoading && isNew && type === 'bot' ? 'loading' : ''} ${isVisible ? 'visible' : ''}`}
      style={{ opacity: isVisible ? 1 : 0 }}
      aria-label={`${type === 'user' ? 'You' : 'AI Assistant'}`}
    >
      {renderContent()}
    </div>
  );
};

export default ChatMessage;
