import React, { useEffect, useRef, useState } from 'react';
import './ChatBubble.css';

const ChatBubble: React.FC = () => {
  const bubbleRef = useRef<HTMLDivElement>(null);
  const [isHovered, setIsHovered] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e: MouseEvent) => {
      if (bubbleRef.current) {
        const rect = bubbleRef.current.getBoundingClientRect();
        const x = e.clientX - rect.left;
        const y = e.clientY - rect.top;
        setMousePosition({ x, y });
      }
    };

    const bubble = bubbleRef.current;
    if (bubble) {
      bubble.addEventListener('mousemove', handleMouseMove);
    }

    return () => {
      if (bubble) {
        bubble.removeEventListener('mousemove', handleMouseMove);
      }
    };
  }, []);

  return (
    <div 
      className="chat-bubble-container"
      ref={bubbleRef}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="chat-bubble">
        <div className="plasma-orb">
          <div className="plasma-core">
            {isHovered && (
              <div className="hover-gif">
                <img 
                  src="https://i.pinimg.com/originals/36/b2/7c/36b27cb0ad7592535d6a939368e4a3ea.gif" 
                  alt="AI Animation"
                />
              </div>
            )}
          </div>
          <div className="plasma-glow"></div>
          <div className="plasma-trail"></div>
        </div>
      </div>
      <div className="cursor-trail"></div>
    </div>
  );
};

export default ChatBubble;
