import React, { useState, useEffect } from 'react';
import './ChatBotUI.css';

const HEADER_CYCLE_TEXTS = [
  'Instant help.',
  'Smarter workflows.',
  'Just Ask Aura.'
];

const ChatBotUI: React.FC<{
  open: boolean;
  onClose: () => void;
}> = ({ open, onClose }) => {
  const [headerIndex, setHeaderIndex] = useState(0);
  const [headerFade, setHeaderFade] = useState(true);

  // Cycle header texts
  useEffect(() => {
    if (!open) return;
    const interval = setInterval(() => {
      setHeaderFade(false);
      setTimeout(() => {
        setHeaderIndex((prev) => (prev + 1) % HEADER_CYCLE_TEXTS.length);
        setHeaderFade(true);
      }, 350);
    }, 1800);
    return () => clearInterval(interval);
  }, [open]);

  // Prevent scroll on body when open
  useEffect(() => {
    if (open) document.body.style.overflow = 'hidden';
    else document.body.style.overflow = '';
    return () => { document.body.style.overflow = ''; };
  }, [open]);

  return (
    <div className={`chatbot-ui-backdrop${open ? ' open' : ''}`}
      onClick={onClose}
      tabIndex={-1}
    >
      <div
        className={`chatbot-ui-window${open ? ' open' : ''}`}
        onClick={e => e.stopPropagation()}
        tabIndex={0}
      >
        <div className="chatbot-ui-header">
          <span className="chatbot-ui-title">5C Aura AI Chatbot</span>
          <span className={`chatbot-ui-header-cycle${headerFade ? ' fade-in' : ' fade-out'}`}>{HEADER_CYCLE_TEXTS[headerIndex]}</span>
          <button className="chatbot-ui-close" onClick={onClose} aria-label="Close">Ã—</button>
        </div>
        <div className="chatbot-ui-messages">
          {/* Placeholder messages for now */}
          <div className="chatbot-msg bot">Hi! I am Aura. How can I help you today?</div>
          <div className="chatbot-msg user">Show me my reports.</div>
          <div className="chatbot-msg bot">Here are your latest reports. <span role="img" aria-label="chart">ðŸ“Š</span></div>
        </div>
        <div className="chatbot-ui-input-row">
          <input className="chatbot-ui-input" placeholder="Type your message..." />
          <button className="chatbot-ui-send">âž¤</button>
        </div>
      </div>
    </div>
  );
};

export default ChatBotUI;
